INSERT INTO `wp_posts` VALUES (247,1,'2024-10-25 13:52:02','2024-10-25 13:52:02','<h3><br><br><br><br><br><br><br>Welcome To Mongol-Tori</h3>		\n			<h3>Mongol-Tori is a next generation mars rover. It is a project by BRAC University which is designed for participation in the University Rover Challenge (URC), an international robotics competition. The rover is built to assist human explorers on Mars by performing tasks like object retrieval, autonomous traversal, equipment servicing, planetary analysis, and life detection<br><br><br><br><br><br><br><br><br><br><br><br><br><br><br></h3>','Home','','inherit','closed','closed','','104-revision-v1','','','2024-10-25 13:52:02','2024-10-25 13:52:02','',104,'http://mongol-tori.local/?p=247',0,'revision','',0);
